import { motion } from 'framer-motion';

export default function About({ setPage }) {
  const team = [
    ['Rolf Mohr', 'Creative Director'],
    ['Herrick Erickson-Brigl', 'Executive Producer'],
    ['Mike Anstine', 'Technical Director'],
    ['Myles Murphy', 'Release Operations']
  ];

  return (
    <section className="container">
      <motion.h1 initial={{opacity:0}} animate={{opacity:1}}>About Us</motion.h1>

      <p>
        Archonik Consulting is a team of industry veterans helping studios
        plan and execute high-quality game releases.
      </p>

      <div className="card-grid">
        {team.map(([name, role]) => (
          <motion.div
            key={name}
            className="card"
            whileHover={{ scale: 1.04 }}
          >
            <h3>{name}</h3>
            <p>{role}</p>
          </motion.div>
        ))}
      </div>

      <button className="btn" onClick={() => setPage('contact')}>
        Contact Us
      </button>
    </section>
  );
}
