import { motion } from 'framer-motion';

export default function Services({ setPage }) {
  return (
    <section className="container">
      <motion.h1 initial={{opacity:0}} animate={{opacity:1}}>
        Our Services
      </motion.h1>

      {[
        'Production Support',
        'Certification and Launch Readiness',
        'Creative and Narrative Consulting',
        'Strategic Planning and Advice'
      ].map(service => (
        <motion.div
          key={service}
          className="card"
          initial={{opacity:0, y:20}}
          whileInView={{opacity:1, y:0}}
        >
          <h3>{service}</h3>
          <p>Expert, modular support tailored to your studio.</p>
        </motion.div>
      ))}

      <button className="btn" onClick={() => setPage('contact')}>
        Contact Us
      </button>
    </section>
  );
}
